var link=document.querySelector(".btn_write"),popup=document.querySelector(".modal_content"),close=document.querySelector(".modal_button_close"),enter=popup.querySelector("[name=name]");link.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("modal_content_show"),enter.focus()}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("modal_content_show")}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal_content_show")&&popup.classList.remove("modal_content_show")});